import React from 'react';
import { useHistory } from 'react-router-dom';
import FormFieldset from './FormFieldset';
import FormInput from './FormInput';
import * as auth from './Auth';
import { setToken, getToken } from '../utils/token';

function Login({ isLoading, handleLogin }) {
  const history = useHistory();

  const [data, setData] = React.useState({
    userEmail: '',
    userPassword: '',
  });

  const handleChange = (e) => {
    const {name, value} = e.target;
    setData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  }

  const submitButtonText = isLoading ? 'Вход...' : 'Войти';

  const handleSubmit = (e) => {
    e.preventDefault();

    const { userEmail, userPassword } = data;

    if(!userEmail || !userPassword) {
      return;
    }

    auth.authorize(userEmail, userPassword)
    .then((data) => {
      if (!data){
        console.log('Что-то пошло не так!');
      }

      console.log(data);
      console.log(getToken('jwt'));
      if (data.jwt) {
        setToken(data.jwt);
        setData({ username: '', password: ''});
        handleLogin(data.userEmail);
        history.push('/');
      }
    })
    .catch(err => console.log(err));
  };

  return(
    <div className="sign-screen">
      <form
        className="sign-screen__form"
        /* name={`${name}`} */
        action="#"
        method="POST"
        onSubmit={handleSubmit}
        noValidate
      >
        <FormFieldset
          formName="form__name_place_sign-screen"
          title="Вход"
          formFieldset="form__fieldset_place_sign-screen"
          submitButton="form__submit-button_place_sign-screen"
          submit={submitButtonText}
        >
          <FormInput
            formFieldName="form__field_place_sign-screen"
            entryFieldName="form__entry-field_place_sign-screen"
            inputId="sign-screen-entry-field-email"
            type="email"
            name="email"
            placeholder="Email"
            value={data.userEmail}
            onChange={handleChange}
            minLength='2'
            maxLength='40'
          >
          </FormInput>
          <FormInput
            formFieldName="form__field_place_sign-screen"
            entryFieldName="form__entry-field_place_sign-screen"
            inputId="sign-screen-entry-field-password"
            type="password"
            name="password"
            placeholder="Пароль"
            value={data.userPassword}
            onChange={handleChange}
            minLength='2'
            maxLength='40'
          >
          </FormInput>
        </FormFieldset>
      </form>
    </div>
  );
}

export default Login
