import React, { useState } from 'react';
import popup__registerIconFailPath from '../images/fail.svg';
import popup__registerIconSuccessPath from '../images/success.svg';


function RegisterPopup({
  isOpen,
  onClose,
  isSuccess
}) {
  const [icon, setIcon] = useState('');
  const [massage, setMessage] = useState('');

  const successRegister = () => {
    if(isSuccess) {
      setIcon(popup__registerIconSuccessPath);
      setMessage('Вы успешно зарегистрировались!')
    } else {
      setIcon(popup__registerIconFailPath);
      setMessage('Что-то пошло не так! Попробуйте ещё раз.')
    }
  }

  successRegister();

  return(
    <section className={`popup popup__register ${isOpen && 'popup_opened'}`}>
      <div
        className="popup__container"
      >
        <img className="popup__register-icon" src={icon} alt="картинка регистрации" />
        <p className="popup__massage-text">
          {massage}
        </p>
        <button
          className="popup__close-button"
          type="button"
          onClick={onClose}
        >
        </button>
      </div>
    </section>
  );
}

export default RegisterPopup
