import React from 'react';
import FormFieldset from './FormFieldset';
import FormInput from './FormInput';
import * as auth from './Auth';
import { Link, useHistory } from 'react-router-dom';

function Register({ isLoading, onRegister }) {
  const history = useHistory();

  const [userEmail, setUserEmail] = React.useState('');
  const [userPassword, setUserPassword] = React.useState('');

  const handleChangeEmail = (e) => {
    setUserEmail(e.target.value);
  };

  const handleChangePassword = (e) => {
    setUserPassword(e.target.value);
  };

  const submitButtonText = isLoading ? 'Регистрация...' : 'Зарегистрироваться';

  const handleSubmit = (e) => {
    e.preventDefault();
    if(!userEmail || !userPassword) {
      return;
    }

    onRegister(userEmail, userPassword);
    setUserEmail('');
    setUserPassword('');
  };

  return(
    <div className="sign-screen">
      <form
        className="sign-screen__form"
        /* name={`${name}`} */
        action="#"
        method="POST"
        onSubmit={handleSubmit}
        noValidate
      >
        <FormFieldset
          formName="form__name_place_sign-screen"
          title="Регистрация"
          formFieldset="form__fieldset_place_sign-screen"
          submitButton="form__submit-button_place_sign-screen"
          submit={submitButtonText}
        >
          <FormInput
            formFieldName="form__field_place_sign-screen"
            entryFieldName="form__entry-field_place_sign-screen"
            inputId="sign-screen-entry-field-email"
            type="email"
            name="email"
            placeholder="Email"
            value={userEmail || ''}
            onChange={handleChangeEmail}
            minLength='2'
            maxLength='40'
          >
          </FormInput>
          <FormInput
            formFieldName="form__field_place_sign-screen"
            entryFieldName="form__entry-field_place_sign-screen"
            inputId="sign-screen-entry-field-password"
            type="password"
            name="password"
            placeholder="Пароль"
            value={userPassword || ''}
            onChange={handleChangePassword}
            minLength='2'
            maxLength='40'
          >
          </FormInput>
        </FormFieldset>
      </form>
      <div className="sign-screen__signin">
        <p className="sign-screen__text">
          Уже зарегистрированы?&nbsp;
        </p>
        <Link
          className="sign-screen__link"
          to="signin"
        >
          Войти
        </Link>
      </div>
    </div>
  );
}

export default Register
