import React from 'react';
import PopupWithForm from './PopupWithForm';
import FormInput from './FormInput';
import { CurrentUserContext } from '../contexts/CurrentUserContext';

function EditAvatarPopup({ isOpen, onClose, onUpdateAvatar, isLoading }) {
  const user = React.useContext(CurrentUserContext);
  const [avatar, setAvatar] = React.useState('')
  const linkRef = React.useRef();

  React.useEffect(() => {
    setAvatar(user.avatar);
  }, [user]);

  const handleChangeAvatar = (e) => {
    setAvatar(e.target.value);
  };

  const submitButtonText = isLoading ? 'Сохранение...' : 'Сохранить';

  const handleSubmit = (e) => {
    e.preventDefault();

    const newAvatarLink = linkRef.current.value;

    onUpdateAvatar({
      link: newAvatarLink
    });
  };

  return(
    <PopupWithForm
      name="update-avatar"
      title="Обновить аватар"
      submit={submitButtonText}
      isOpen={isOpen}
      onClose={onClose}
      onSubmit={handleSubmit}
      formName="popup__container-name"
      formFieldset="popup__container-form"
      submitButton="popup__submit-button"
    >
      <FormInput
        inputId="popup__entry-field_avatar-image-url"
        type="url"
        name="link"
        placeholder=""
        value=''
        onChange={handleChangeAvatar}
        minLength=''
        maxLength=''
      >
      </FormInput>
    </PopupWithForm>
  );
}

export default EditAvatarPopup

