import React from 'react';
import { Link } from 'react-router-dom';
import header__logoPath from '../images/logo/logo-white.svg';

function Header({ isLoginOpen, isRegisterOpen, onRegister, onLogin, onSignOut }) {
  let linkTo = '';
  let buttonName = '';
  let buttonClick = '';

  const setButtonProps = () => {
    if(isLoginOpen) {
      linkTo = "signup";
      buttonName = "Регистрация";
      buttonClick = onRegister;

    } else if(isRegisterOpen) {
      linkTo = "signin";
      buttonName = "Вход";
    }
  };

  setButtonProps();

  return (
    <header className="header">
      <img className="header__logo" src={header__logoPath} alt="логотип заголовка" />
      <Link
        className="header__link"
        to={linkTo}
        onClick={buttonClick}
      >
        {buttonName}
      </Link>
    </header>
  );
}

export default Header
