import React from 'react';

function FormInput({
  inputId,
  type,
  name,
  placeholder,
  value,
  defaultValue,
  ref,
  onChange,
  minLength,
  maxLength,
  }) {
  return(
    <label className="form__field">
      <input
        className={`form__entry-field ${inputId}`}
        id={inputId}
        type={type}
        name={name}
        placeholder={placeholder}
        value={value}
        onChange={onChange}
        minLength={minLength}
        maxLength={maxLength}
        required
      />
      <span
        className="form__entry-field-error"
        id={`${inputId}-error`}>
      </span>
    </label>
  );
}

export default FormInput
