import React, { useEffect, useState } from 'react';
import { Link, useLocation, useHistory} from 'react-router-dom';
import classNames from 'classnames';
import header__logoPath from '../images/logo/logo-white.svg';

function Header({ onRegister, onLogin, onSignOut, loggedIn, userEmail }) {
  /* let linkTo = '';
  let buttonName = '';
  let buttonClick;

  if(window.location.pathname === '/signin') {
    linkTo = '/signup'
    buttonName = 'Регистрация'
    buttonClick = onRegister;
  }

  if(window.location.pathname === '/signup') {
    linkTo = '/signin'
    buttonName = 'Вход'
    buttonClick = onLogin;
  } */
  /* const [buttonClick, setButtonClick] = useState(''); */
  const location = useLocation();
  const history = useHistory();
  console.log(history.location.pathname);

  const [buttonName, setButtonName] = useState('');
  const [linkTo, setLinkTo] = useState('');
  const [email, setEmail] = useState('');

  useEffect(() => {
    if(location.pathname === '/signup') {
      /* setButtonClick(''); */
      setButtonName('Войти');
      setLinkTo('/signin');
      setEmail('');
    } else if(location.pathname === '/signin') {
      /* setButtonClick(''); */
      setButtonName('Регистрация');
      setLinkTo('/signup');
      setEmail('');
    } else if(history.location.pathname === '/' && loggedIn) {
      /* setButtonClick(onSignOut); */
      setButtonName('Выйти');
      setLinkTo('/signin');
      setEmail(userEmail);
    }
  }, []);

  return (
    <header className="header">
      <img className="header__logo" src={header__logoPath} alt="логотип заголовка" />
      <div className="header__text">
        <p className="header__email">
          {email}
        </p>
        <Link
          className={classNames('header__link', { 'header__link_loggedIn' : loggedIn })}
          to={linkTo}
          /* onClick={buttonClick} */
        >
          {buttonName}
        </Link>
      </div>
    </header>
  );
}

export default Header
