const TOKEN_KEY = 'jwt';

export const setToken = (token) => {
  console.log(token)
  localStorage.setItem(TOKEN_KEY, token)
};

export const getToken = (TOKEN_KEY) => localStorage.getItem(TOKEN_KEY);

export const removeToken = () => localStorage.removeItem(TOKEN_KEY);
